mapper.select:
SELECT
    a.ID, a.DATE, a.MONEY, a.MONEY_STATE, a.TYPE, a.COMMENT, a.DEL_STATE, a.ITEM_NAME, b.TYPE_ONE_NAME
FROM
    account_bill a
  LEFT JOIN account_bill_type b ON a.TYPE = b.TYPE_ID

      DEL_STATE = '1'

         AND a.ITEM_NAME LIKE concat('%', #{ itemName }, '%')


         AND date_format(a.DATE, '%Y-%m') = date_format(#{ yearMonth }, '%Y-%m')


          AND a.DATE = #{ date }


          AND a.TYPE = #{ type }


          AND a.MONEY_STATE = #{ moneyState }


          AND a.MONEY <= #{ moneyMax }


          AND a.MONEY >= #{ moneyMin }


          AND a.ITEM_NAME NOT REGEXP #{ filterKeyword }

mapper.delete:
DELETE FROM account_bill
WHERE
    YEAR ( DATE ) = #{ year }
    AND MONTH ( DATE ) = #{ month }

mapper.select:
SELECT


            b.TYPE_ONE_NAME typeOneName,


            DATE_FORMAT( DATE, '%Y-%m-01' ) fdate,


    SUM( MONEY ) money
FROM
    account_bill a

        LEFT JOIN account_bill_type b ON a.TYPE = b.TYPE_ID


    DEL_STATE = '1'

        AND b.TYPE_ONE_NAME IS NOT NULL


        AND MONEY_STATE = #{ moneyState }


        AND MONEY <= #{ moneyMax }


        AND MONEY >= #{ moneyMin }


        AND DATE >= #{ startTime }


        AND DATE <= #{ endTime }


        AND ITEM_NAME NOT REGEXP #{ filterKeyword }


GROUP BY

        fdate


        TYPE

SELECT
    a.DATE,
    SUM( a.MONEY ) MONEY
FROM
    (
      SELECT DATE, MONEY, MONEY_STATE, TYPE, COMMENT, DEL_STATE, ITEM_NAME, USER_ID FROM account_bill

          DEL_STATE = '1'

              AND ITEM_NAME LIKE concat('%', #{ itemName }, '%')


              AND YEAR(DATE) = #{ year }


              AND TYPE = #{ type }


              AND MONEY_STATE = #{ moneyState }


              AND MONEY <= #{ moneyMax }


              AND MONEY >= #{ moneyMin }


              AND ITEM_NAME NOT REGEXP #{ filterKeyword }


    ) a
GROUP BY
    a.DATE
